<?	class controller_admin_common extends base_controller {		public function __construct() {			parent::__construct();			if ('login' == base_router::$controller) {				if ($this->user->is_logged) {					header('Location: ' . ROOT_URL . 'admin');					exit;				}			}			else {				if (0 == $this->user->is_logged) {					header('Location: ' . ROOT_URL . 'admin/login');					exit;				}			}		}		public function index() {}	}